import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import React, { useContext, useState } from "react";
import { TYPES } from "../reducer/authReducer";

import style from "src/pages/style.css";



const Login = () => {
  const navigate = useNavigate();
  const { dispatch } = useContext(AuthContext);

  // const {setUser : setUsername, setIsAuth} = useContext(AuthContext);

  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const { email } = user;
    const data = {
      user: {
        username: "nico",
        email: email,
      },
      token: "1233324125145",
    };

    dispatch({ type: TYPES.LOGIN, payload: data });

    navigate("/Browse", {
      replace: true,
    });
  };

  return (
    <>
      <div className="container">
        <a className="logo">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/7/7a/Logonetflix.png"
            alt="logo"
            className="logo-img"
          />
        </a>

        <div className="login">
          <h1 className="login_title">Iniciar Sesión</h1>
          <form onSubmit={handleSubmit} className="login_group">
            <input
              type="text"
              name="email"
              placeholder="Email"
              onChange={handleChange}
              value={user.email}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              className="login_input"
              onChange={handleChange}
              value={user.password}
            />
            <button type="submit" className="login_sing-in">
              Iniciar sesión
            </button>
          </form>
          <div className="login_secondary">
            <a href="#">
              <input type="checkbox" className="checkbox"></input>Recordarme
            </a>
            <a href="#">¿Necitas ayuda?</a>
          </div>
        </div>

        <footer className="footery">
          <div className="center">
            <a href="#" className="footer-question">
              ¿Preguntas? Llama al 0800 345 1593
            </a>

            <div className="footer-links">
              <a href="#">Preguntas frecuentes</a>
              <a href="#">Centro de ayuda</a>
              <a href="#">Términos de uso</a>
              <a href="#">Privacidad</a>
            </div>
            <div className="footer-links">
              <a href="#">Preferencias de cookies</a>
              <a href="#">Información corporativa</a>
            </div>
          </div>
        </footer>
      </div>
    </>
  );
};

export default Login;
