import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import React, { useContext, useState } from "react";
import { TYPES } from "../reducer/authReducer";

const Login = () => {
  const navigate = useNavigate();
  const { dispatch } = useContext(AuthContext);

  // const {setUser : setUsername, setIsAuth} = useContext(AuthContext);

  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const { email } = user;
    const data = {
      user: {
        username: "nico",
        email: email,
      },
      token: "1233324125145",
    };

    dispatch({ type: TYPES.LOGIN, payload: data });

    navigate("/Browse", {
      replace: true,
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          value={user.email}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          value={user.password}
        />
        <button type="submit" className="login_sing-in">Iniciar sesión</button>

        <footer className="footery">
          <div className="center">
            <a href="#" className="footer-question">
              ¿Preguntas? Llama al 0800 345 1593
            </a>

            <div className="footer-links">
              <a href="#">Preguntas frecuentes</a>
              <a href="#">Centro de ayuda</a>
              <a href="#">Términos de uso</a>
              <a href="#">Privacidad</a>
            </div>
            <div className="footer-links">
              <a href="#">Preferencias de cookies</a>
              <a href="#">Información corporativa</a>
            </div>
          </div>
        </footer>
      </form>
    </div>
  );
};

export default Login;
