import { Navigation, Pagination } from "swiper";

export const sliderProps = {
  slidesPerView: 6,
  slidesPerGroup: 4,
  breakpoints: {
    1440: {
      slidesPerView: 6,
      slidesPerGroup: 4,
    },
    1240: {
      slidesPerView: 5,
      slidesPerGroup: 3,
    },
    1025: {
      slidesPerView: 4,
      slidesPerGroup: 3,
    },
    
    769: {
      slidesPerView: 2,
      slidesPerGroup: 2,
    },
  },
  grabCursor: true,
  scrollbar: true,
  navigation: true,
//   pagination: {
//     clickable: true,
//   },
  modules: [Navigation, Pagination],
};
