import React from "react";
import { moviesAdapter } from "../../features/movies/utils/moviesAdapter";

import styles from "./Banner.module.css";

const Banner = ({ item }) => {
  return (
    <div
    style={{backgroundImage: `url(${item?.backdrop})`}}
    className={styles.bannerprincipal}
    > 
    
    <div className={styles.container}>
			<div className={styles.w_90}>
				<h3><span className={styles.n}>N</span> Movie</h3>
				<p>
				</p>
				<h2>{item?.name}</h2>
        {item?.overview}
				<a href="#"><i class="play fas fa-play"></i>Play</a>
				<a href="#"><i class="play fas fa-plus"></i>Mi list</a>
			</div>
		</div>

	</div>
  
  );
};

export default Banner;
