import React from "react";

import styles from "./Browse.module.css";

const Banner = ({ item }) => {
  return (
    <div
      style={{
        height: "80vh",
        backgroundImage: `url(${item?.backdrop})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        boxShadow: '10px 5px 5px black',
            }}
    > 
    
    
    </div>
  );
};

export default Banner;
