import React from "react";

import styles from "./Banner.module.css";

const Banner = ({ item }) => {
  return (
    <div
    style={{backgroundImage: `url(${item?.backdrop})`}}
    className={styles.bannerprincipal}
    > 
    
    <div className={styles.container}>
			<div class="w-90">
				<h3><span class="n">N</span> Serie</h3>
				<h2>LA CASA DE PAPEL</h2>
				<a href="#"><i class="play fas fa-play"></i>Reproducir</a>
				<a href="#"><i class="play fas fa-plus"></i>Mi lista</a>
				<p>
					Una banda organizada de ladrones tiene el objetivo de cometer el atraco 
					del siglo en la Fábrica Nacional de Moneda y Timbre. Cinco meses de 
					preparación quedarán reducidos a once días para poder llevar a cabo 
					con éxito el gran golpe.
				</p>
			</div>
			<div class="w-10 r">
				<p>R</p>
			</div>
		</div>

	</div>
  
  );
};

export default Banner;
